mapper.select:
select return_id as returnId, pur_cnt as purCnt, client, s.sales_nm as salesNm, return_cnt as returnCnt, price, date_format( purdate,'%Y-%m-%d') as purdate, date_format(r.regdate,'%Y-%m-%d') as regdate, r.confirmYN from tb_returndir r inner join tb_refundinfo rf on r.rfinfo_id = rf.rfinfo_id inner join tb_purchaseinfo pf on rf.purinf_id = pf.purinf_id inner join tb_purchase p on p.pur_id = pf.pur_id inner join tb_userinfo u on u.loginID = p.loginID inner join tb_sales s on pf.sales_id = s.sales_id
and s.sales_nm Like CONCAT('%', #{salesNm}, '%')
AND DATE_FORMAT(r.regdate, '%Y-%m-%d') >= #{from_date}
AND DATE_FORMAT(r.regdate, '%Y-%m-%d') <= #{to_date}
AND confirmYN is null
ORDER BY r.regdate ASC LIMIT #{pageIndex}, #{pageSize}

/*kr.happy.study.system.dao.RefundConfirmDao.RefundConfirmCnt*/ SELECT COUNT(*) from tb_returndir r inner join tb_refundinfo rf on r.rfinfo_id = rf.rfinfo_id inner join tb_purchaseinfo pf on rf.purinf_id = pf.purinf_id inner join tb_purchase p on p.pur_id = pf.pur_id inner join tb_userinfo u on u.loginID = p.loginID inner join tb_sales s on pf.sales_id = s.sales_id
and s.sales_nm Like CONCAT('%', #{salesNm}, '%')
AND DATE_FORMAT(r.regdate, '%Y-%m-%d') >= #{from_date}
AND DATE_FORMAT(r.regdate, '%Y-%m-%d') <= #{to_date}
AND confirmYN is null

mapper.update:
/*kr.happy.study.system.dao.RefundConfirmDao.updaterefundConfirm*/
UPDATE tb_returndir SET
		confirmYN = #{confirmYN}
WHERE	return_id = #{returnId}
