mapper.select:
select
ui.client, SUM(pi.pur_cnt*sa.price) sell from tb_delivdir dlv inner join tb_purchaseinfo pi on dlv.purinf_id = pi.purinf_id inner join tb_userinfo ui on pi.loginID = ui.loginID inner join tb_sales sa on pi.sales_id = sa.sales_id inner join tb_purchase pur on pi.pur_id = pur.pur_id where dlv.deliverStatus = 'U' and ui.user_Type='C' and dlv.return_id is null
AND ui.client LIKE CONCAT('%', #{client}, '%')
AND DATE_FORMAT(pur.purdate, '%Y-%m-%d') >= #{from_date}
AND DATE_FORMAT(pur.purdate, '%Y-%m-%d') <= #{to_date}
GROUP BY ui.client ORDER BY sell DESC LIMIT #{pageIndex}, #{pageSize}

SELECT count(*) FROM ( select ui.client from tb_delivdir dlv inner join tb_purchaseinfo pi on dlv.purinf_id = pi.purinf_id inner join tb_userinfo ui on pi.loginID = ui.loginID inner join tb_sales sa on pi.sales_id = sa.sales_id inner join tb_purchase pur on pi.pur_id = pur.pur_id where dlv.deliverStatus = 'U' and ui.user_Type='C' and dlv.return_id is null
AND ui.client LIKE CONCAT('%', #{client}, '%')
AND DATE_FORMAT(pur.purdate, '%Y-%m-%d') >= #{from_date}
AND DATE_FORMAT(pur.purdate, '%Y-%m-%d') <= #{to_date}
GROUP BY ui.client) B
