mapper.insert:
	INSERT
		 INTO tb_orderinfo
		 		(	
		 	  		order_cnt
		 		,	pur_id
		 		,	loginID
		 		,	sales_id
    		    , 	comp_id
				)
		VALUES (
			    	#{com_cnt}
		        ,	#{pur_id}
		        , 	#{loginID}
		        ,	#{sales_id}
		        , 	#{com_code}
				)


 INSERT 
 INTO tb_orderdir
 		(	
 			regdate
 		,	order_id
		)
VALUES (
			now()
        ,	#{fk}
		);

	insert into tb_returndir
    		(	regdate 
    		, 	return_cnt
    		, 	dir_result
    		, 	rfinfo_id
    		)
    values 	(	now()
    		, 	#{return_cnt}
    		, 	'O'
    		,  (
    			select rinfo.rfinfo_id
		          from tb_refundinfo rinfo
		            join tb_purchaseinfo pinfo ON pinfo.purinf_id = rinfo.purinf_id
		              where pinfo.pur_id = #{pur_id}
                )
    		)
